const sidebar = () => {
  const scrollable = document.querySelector('.page-container .scrollable'),
    scrollContainer = document.querySelector('.page-container.scroll-container');
  // console.log('Scrollable: ', scrollable, scrollContainer);
  let scrolled = false, prevScrollValue = 0;
  if (scrollable && scrollContainer) scrollable.addEventListener('scroll', ev => {
    scrolled = prevScrollValue < ev.target.scrollTop;
    prevScrollValue = ev.target.scrollTop;
    if (scrolled) scrollContainer.classList.add('page-scrolled');
    else if (prevScrollValue < 32) scrollContainer.classList.remove('page-scrolled');
  });
};